mapper.select:
SELECT

FROM t_user


    AND  uid = #{uid,jdbcType=INTEGER}


    AND  uname LIKE CONECT(`%`, #{uname,jdbcType=VARCHAR},`%`)


    AND  username LIKE CONECT(`%`, #{username,jdbcType=VARCHAR},`%`)


    AND  age = #{age,jdbcType=SMALLINT}


    AND  sex = #{sex,jdbcType=VARCHAR}


    AND  address LIKE CONECT(`%`, #{address,jdbcType=VARCHAR},`%`)
